def get_keywords_for_selection(selected_id):
    """
    Get keywords for a selected domain or category ID
    
    Args:
        selected_id (str): Either a domain ID (e.g. 'btech_cse') or category ID (e.g. 'software_development')
    
    Returns:
        list: Relevant keywords for project scoring
    """
    # Check if it's a category ID
    if selected_id in CATEGORY_KEYWORDS:
        return CATEGORY_KEYWORDS[selected_id]
    
    # Check if it's a domain ID
    if selected_id in DOMAIN_MAPPING:
        # Return keywords from all categories in this domain
        domain_keywords = []
        for category_id in DOMAIN_MAPPING[selected_id]:
            if category_id in CATEGORY_KEYWORDS:
                domain_keywords.extend(CATEGORY_KEYWORDS[category_id])
        return list(set(domain_keywords))  # Remove duplicates
    
    # Fallback to advanced keywords
    return get_advanced_keywords()

def get_advanced_keywords():
    """Get filtered list of advanced technology keywords"""
    advanced_terms = [
        'docker', 'kubernetes', 'microservice', 'cloud', 'aws', 'azure', 'gcp',
        'machine learning', 'ai', 'ml', 'deep learning', 'neural network',
        'react', 'angular', 'vue', 'node.js', 'spring boot', 'django',
        'architecture', 'scalable', 'pipeline', 'ci/cd', 'devops',
        'mongodb', 'postgresql', 'redis', 'elasticsearch',
        'automation', 'testing', 'monitoring', 'security',
        'real-time', 'streaming', 'kafka', 'websocket', 'api', 'rest',
        'tensorflow', 'pytorch', 'scikit-learn', 'spark', 'hadoop',
        'blockchain', 'smart contract', 'cryptocurrency', 'fintech',
        'advanced', 'system design', 'performance', 'optimization'
    ]
    
    return [
        keyword for keyword in ALL_KEYWORDS 
        if any(term in keyword for term in advanced_terms)
    ]

def get_all_keywords():
    """Get all keywords from domainData.js"""
    return ALL_KEYWORDS

# Backward compatibility
def get_domain_keywords(domain_id):
    """Legacy function - use get_keywords_for_selection instead"""
    return get_keywords_for_selection(domain_id)
